import React from 'react';
import { LandingView } from './components/landing/LandingView';
import { OnboardingView } from './components/onboarding/OnboardingView';
import { SellerDashboard } from './components/seller/SellerDashboard';
import { BuyerDashboard } from './components/buyer/BuyerDashboard';
import { DealWorkflow } from './components/deal/DealWorkflow';
import { usePlatformState } from './hooks/usePlatformState';
import { buyerProfiles, sellerQuestions, buyerQuestions } from './data/mockData';

const App = () => {
    const {
        currentView,
        setCurrentView,
        userType,
        onboardingStep,
        setOnboardingStep,
        selectedBuyer,
        setSelectedBuyer,
        matches,
        setMatches,
        dealStage,
        setDealStage,
        aiAnalysis,
        updateFormData,
        startOnboarding,
        simulateAIAnalysis
    } = usePlatformState();

    const nextStep = () => {
        const totalSteps = userType === 'seller' ? sellerQuestions.length : buyerQuestions.length;
        if (onboardingStep < totalSteps - 1) {
            setOnboardingStep(onboardingStep + 1);
        } else {
            setCurrentView(userType === 'seller' ? 'sellerDashboard' : 'buyerDashboard');
        }
    };

    const prevStep = () => {
        if (onboardingStep > 0) {
            setOnboardingStep(onboardingStep - 1);
        }
    };

    const handleMatch = (buyerId: number) => {
        setMatches(prev => [...prev, buyerId]);
        setTimeout(() => {
            setCurrentView('dealWorkflow');
            setSelectedBuyer(buyerProfiles.find(b => b.id === buyerId) || null);
        }, 1000);
    };

    switch (currentView) {
        case 'onboarding':
            return (
                <OnboardingView
                    questions={userType === 'seller' ? sellerQuestions : buyerQuestions}
                    currentStep={onboardingStep}
                    userType={userType}
                    onNext={nextStep}
                    onPrevious={prevStep}
                    onFieldChange={updateFormData}
                />
            );
        case 'sellerDashboard':
            return (
                <SellerDashboard
                    buyers={buyerProfiles}
                    matches={matches}
                    onMatch={handleMatch}
                />
            );
        case 'dealWorkflow':
            return (
                <DealWorkflow
                    selectedBuyer={selectedBuyer}
                    dealStage={dealStage}
                    aiAnalysis={aiAnalysis}
                    onBackClick={() => setCurrentView('sellerDashboard')}
                    onStageChange={setDealStage}
                    onDocumentUpload={simulateAIAnalysis}
                />
            );
        case 'buyerDashboard':
            return (
                <BuyerDashboard onBackToLanding={() => setCurrentView('landing')} />
            );
        default:
            return (
                <LandingView onStartOnboarding={startOnboarding} />
            );
    }
};

export default App;
